import { Box, ListItem, Text, UnorderedList } from "@chakra-ui/react";
import { NavLink } from "react-router-dom";
import { ButtonCustom } from "../../../components/Buttton/ButtonCustom";
import classes from "./MainNavigation.module.css";
import { nav } from "./items";

import { useLocation } from "react-router-dom";
import { Link as ScrollLink, scroller } from "react-scroll";
import { useEffect } from "react";

function MainNavigation() {
  const scrollLinkProps = {
    activeStyle: { color: "white" },
    spy: true,
    smooth: true,
    duration: 500,
    offset: -76,
    hashSpy: true,
  };

  const { pathname, hash } = useLocation();

  return (
    <Box className={classes.mainNavigation}>
      <UnorderedList>
        {nav[0].content.map((item) => {
          console.log(
            "`${pathname}#${item.link}` = ",
            `${pathname}#${item.link}`
          );

          return (
            <ListItem>
              {" "}
              <ScrollLink key={item.link} {...scrollLinkProps} to={item.link}>
                {/* <ButtonCustom text={item.label}></ButtonCustom> */}
              </ScrollLink>
            </ListItem>
          );
        })}
      </UnorderedList>
    </Box>
  );
}

export default MainNavigation;
