import { ButtonRed } from "../ButtonRed";

function Footer() {
  return (
    <Box fontSize={"60px"} bg={"gray.800"} display={"flex"} flexDirection={"column"} gap={"20px"} borderRadius={"20px 20px 0px 0px"} color={"white.900"} p={"40px"}>
      <Box textAlign={"left"} marginLeft={"30px"}>
        <Text>Остались вопросы?</Text>
      </Box>
      <Box  display={"flex"} flexDirection={"row"} gap={"20px"} >
          <Text textAlign={"center"}>Пиши!</Text> 
          <ButtonRed text={"Перейти в Telegram"}/>
      </Box>


    </Box>

  );
}

export default Footer;
