import { Box } from '@chakra-ui/react'
import { NavLink } from "react-router-dom";
import { ButtonCustom } from "../../../components/Button";
import classes from './MainNavigation.module.css';

import { Link, useLocation } from 'react-router-dom';
import { Link as ScrollLink, scroller } from 'react-scroll';
import { useEffect } from 'react';

function MainNavigation() {
  const scrollLinkProps = {
    activeStyle: { color: 'white' },
    spy: true,
    smooth: true,
    duration: 500,
    offset: -76,
    hashSpy: true,
  };

const { pathname, hash } = useLocation();

  useEffect(() => {
    scroller.scrollTo(hash.slice(1), scrollLinkProps);
  }, [pathname]);

    const isTheSamePage = pathname === menu.pathname;
    const LinkComponent = isTheSamePage ? ScrollLink : Link;
  //   <LinkComponent
  //   to={isTheSamePage ? link.link : `${menu.pathname}#${link.link}`}
  //   key={link.link}
  //   {...scrollLinkProps}
  // >
  return (
      <nav className={classes.mainNavigation}>
        <ul >
          <LinkComponent>
            <NavLink to={isTheSamePage ? link.link : `abc`}><ButtonCustom text="Об обучении"/></NavLink>
          </LinkComponent>

          <LinkComponent>
            <NavLink to="/advantages"><ButtonCustom text="Преимущества"/></NavLink>
          </LinkComponent>

          <LinkComponent>
            <NavLink to="/feedbacks"><ButtonCustom text="Отзывы"/></NavLink>
          </LinkComponent>

          <LinkComponent>
            <NavLink to="/prices"><ButtonCustom text="Ценовая политика"/></NavLink>
          </LinkComponent>
        </ul>
      </nav>
  );
}

export default MainNavigation;
