import { Box, Text } from '@chakra-ui/react';
import { ButtonRed } from '../ButtonRed';
import { Header } from '../../layout/components/Header';
import MainNavigation from '../../layout/components/MainNavigation';


function HeroBanner() {
return (
<Box >
<Box className='thisOne' display={"flex"} flexDirection={"column"}   bg={"#302D3D"}   borderRadius={"20px"}
lineHeight={"94.08px"}
> 
       <Header />
       <Box 
           color={"#DEDCE7"}
           fontSize={"110px"}
           fontFamily={"Inter"}
           fontWeight={500}
           display={"flex"} 
           flexDirection={"column"} 
       >
           <Text m={"23px 0px 0 90px;"} justifyContent={"left"}>Становись</Text>
           <Text m={"-110px 0px 0 0px;"}    
                 transform={"rotate(-2deg)"}
                 flexDirection={"row"} 
                 display={"flex"}
                 justifyContent={"center"}
                 >
               <Text bg={"red"} w={"328px"}>{'{Java}'}</Text>
               <Text>-разработчиком</Text>
           </Text>
           <Text 
           margin={"-153px 0 0 0px"}
           justifyContent={"right"}
           display={"flex"}
            >вместе с нами</Text>
       </Box>
       <Box >
             <Text w={"266px"} m={"0 0 0 0px"}>
                   Получи классную востребованную профессию и зарабатывай дома в удобное время
             </Text>
           <ButtonRed text="Начать учиться"/>
       </Box>
       <MainNavigation  /> 
</Box>

</Box>
 );
}


export default HeroBanner;
