import { Box, Text } from "@chakra-ui/react";
import { ButtonRed } from "../ButtonRed";
import { Header } from "../../layout/components/Header";
import MainNavigation from "../../layout/components/MainNavigation";

function HeroBanner() {
  return (
    <Box>
      <Box
        className="thisOne"
        display={"flex"}
        flexDirection={"column"}
        bg={"#302D3D"}
        borderRadius={"20px"}
        alignItems={"center"}
      >
        <Header />

        <Box
          color={"#DEDCE7"}
          fontSize={"110px"}
          fontFamily={"Inter"}
          fontWeight={500}
          lineHeight={"15px"}
          display={"flex"}
          flexDirection={"column"}
        >
          <Text m={"78px 0px 0px 58px"} >Становись</Text>
          <Text
            m={" 0px 0px"}
            transform={"rotate(-2deg)"}
            flexDirection={"row"}
            display={"flex"}
            alignItems={"center"}
          >
            <Text bg={"red"} w={"328px"} m={"0px 0px"}>{"{Java}"}</Text>
                <Text>-разработчиком</Text>
            </Text>
            <Text justifyContent={"right"} display={"flex"} marginTop={"-27px"}>вместе с нами</Text>
        </Box>

        <Box   display={"flex"}  flexDirection={"column"} color={"#FFFFFF"} fontSize={"20px"} alignItems={"center"} lineHeight={"0px"}>
          <Text  w={"325px"}
          > 
            Получи классную востребованную профессию и зарабатывай дома в
            удобное время
          </Text>
          <ButtonRed text="Начать учиться" />
        </Box>
        <MainNavigation />
      </Box>
    </Box>
  );
}

export default HeroBanner;
