import { NavLink } from "react-router-dom";
import { ButtonCustom } from "../../../components/Button";
import classes from './MainNavigation.module.css';
import { nav } from './items';

import { useLocation } from 'react-router-dom';
import { Link as ScrollLink, scroller } from 'react-scroll';
import { useEffect } from 'react';

function MainNavigation() {
  const scrollLinkProps = {
    activeStyle: { color: 'white' },
    spy: true,
    smooth: true,
    duration: 500,
    offset: -76,
    hashSpy: true,
  };

const { pathname, hash } = useLocation();

  useEffect(() => {
    scroller.scrollTo(hash.slice(1), scrollLinkProps);
  }, [pathname]);

  return ( <Box className={classes.mainNavigation}>
    <UnorderedList >
    {
    nav[0].content.map((item) => {

          return (<ListItem 
             > <NavLink   key={item.link}
             {...scrollLinkProps}
             to={`${pathname}#${item.link}`}></NavLink><ButtonCustom text={item.label} ></ButtonCustom>
           </ListItem>

        )
    })}
    </UnorderedList>
    </Box>

  )
  }


