import { Box, Text } from "@chakra-ui/react";
import { ButtonRed } from "../ButtonRed";
import { Header } from "../../layout/components/Header";
import MainNavigation from "../../layout/components/MainNavigation";

function HeroBanner() {
  return (
    <Box>
      <Box
        className="thisOne"
        display={"flex"}
        flexDirection={"column"}
        bg={"#302D3D"}
        borderRadius={"20px"}
      >
        <Header />
        <Box
          color={"#DEDCE7"}
          fontSize={"110px"}
          fontFamily={"Inter"}
          fontWeight={500}
          display={"flex"}
          flexDirection={"column"}
          m={"0px 0px"}
          alignItems={"center"}
        > 
          <Text  justifyContent={"left"} m={"20px 0px 0px 0px"}>
            Становись
          </Text>
          <Text

            transform={"rotate(-2deg)"}
            flexDirection={"row"}
            display={"flex"}

          >
            <Text bg={"red"} w={"328px"}> 
              {"{Java}"}
            </Text>
            <Text>-разработчиком</Text>
          </Text>
          <Text justifyContent={"right"} display={"flex"}>
            вместе с нами
          </Text>
        </Box>
        <Box>
          <Text w={"266px"} >
            Получи классную востребованную профессию и зарабатывай дома в
            удобное время
          </Text>
          <ButtonRed text="Начать учиться" />
        </Box>
        <MainNavigation />
      </Box>
    </Box>
  );
}

export default HeroBanner;
